function loadGraphics()
    Bahnschrift_sm = love.graphics.newFont("/fonts/BAHNSCHRIFT.TTF", 30)
    Bahnschrift_lg = love.graphics.newFont("/fonts/BAHNSCHRIFT.TTF", 100)

    Squaracter = love.graphics.newImage("images/squaracter.png")

    Arrow = love.graphics.newImage("images/arrow.png")
    Switch = love.graphics.newImage("images/switch.png")
end

function loadAudio()
    -- Sound Effects generated by https://sfxr.me/
    Step = love.audio.newSource("sounds/step2.wav", "static")
    Bump = love.audio.newSource("sounds/bump.wav", "static")
    JumpSound = love.audio.newSource("sounds/jump.wav", "static")
    SmallLanding = love.audio.newSource("sounds/landing_small.wav", "static")
    LargeLanding = love.audio.newSource("sounds/landing_large.wav", "static")
    Slide = love.audio.newSource("sounds/slide.wav", "static")
    Slide:setLooping(true)
    Walljump = love.audio.newSource("sounds/walljump.wav", "static")
    SwitchSound = love.audio.newSource("sounds/switch.wav", "static")
    SwitchMeter = love.audio.newSource("sounds/switch_meter.wav", "static")
    SwitchMeter:setLooping(true)
    SwitchReady = love.audio.newSource("sounds/switch_ready.wav", "static")
    Pause = love.audio.newSource("sounds/pause.wav", "static")
    Unpause = love.audio.newSource("sounds/unpause.wav", "static")
end